/*
		1- faça uma função que retorne a soma de três números inteiros com parâmetro.
		
		
		
		
 */


// ***EXERCICIO 1***

public class ExerciciosRevisão {

	public static int soma3Parcelas (int p1, int p2, int p3) {
		return (p1+p2+p3);

	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=4, b=5, c=2, soma;
		soma = soma3Parcelas(a,b,c);

		System.out.println(" A soma é: "+soma);



	}

}
